vector<int> common(vector<int> l1, vector<int> l2) {
    // Step 1: Sort both vectors
    sort(l1.begin(), l1.end());
    sort(l2.begin(), l2.end());

    // Step 2: Initialize pointers and a set for common elements
    int i = 0, j = 0;
    set<int> commonElements;

    // Step 3: Use two-pointer technique to find common elements
    while (i < l1.size() && j < l2.size()) {
        if (l1[i] == l2[j]) {
            commonElements.insert(l1[i]);
            i++;
            j++;
        } else if (l1[i] < l2[j]) {
            i++;
        } else {
            j++;
        }
    }

    // Step 4: Convert the set to a vector
    vector<int> result(commonElements.begin(), commonElements.end());

    // Step 5: Return the result
    return result;
}

