int fibfib(int n) {
    if (n == 0 || n == 1) return 0;
    if (n == 2) return 1;

    vector<int> dp(n + 1);
    dp[0] = 0;
    dp[1] = 0;
    dp[2] = 1;

    for (int i = 3; i <= n; ++i) {
        dp[i] = dp[i - 1] + dp[i - 2] + dp[i - 3];
    }

    return dp[n];
}


