string circular_shift(int x, int shift) {
    // Convert the integer to a string
    string s = to_string(x);

    // Calculate the number of digits
    int n = s.length();

    // Calculate the effective shift
    int effective_shift = shift % n;

    // If the effective shift is zero, reverse the string
    if (effective_shift == 0) {
        reverse(s.begin(), s.end());
    } else {
        // Perform the circular shift
        rotate(s.rbegin(), s.rbegin() + effective_shift, s.rend());
    }

    // Return the resulting string
    return s;
}

// Function to test the circular_shift function
void test_circular_shift() {
    cout << "circular_shift(12, 1) = " << circular_shift(12, 1) << " (Expected: 21)" << endl;
    cout << "circular_shift(12, 2) = " << circular_shift(12, 2) << " (Expected: 12)" << endl;
    cout << "circular_shift(12345, 2) = " << circular_shift(12345, 2) << " (Expected: 45123)" << endl;
    cout << "circular_shift(12345, 7) = " << circular_shift(12345, 7) << " (Expected: 54321)" << endl;
}


