int fruit_distribution(string s, int n) {
    int apples = 0, oranges = 0;
    stringstream ss(s);
    string word;

    while (ss >> word) {
        if (word == "apples") {
            ss >> word; // Skip "and"
            ss >> word; // Read the number of oranges
            oranges = stoi(word);
        } else if (word == "oranges") {
            break; // No need to continue parsing after "oranges"
        } else {
            apples = stoi(word); // Read the number of apples
        }
    }

    int mangoes = n - (apples + oranges);
    return mangoes;
}


