    }

    // Step 3: Initialize result to -1
    int result = -1;

    // Step 4: Iterate through the frequency map
    for (const auto& entry : frequencyMap) {
        int num = entry.first;
        int freq = entry.second;

        // Check if the frequency is greater than or equal to the value
        if (freq >= num) {
            // Update result to the maximum of the current result and the integer
            result = max(result, num);
        }
    }

    // Step 5: Return the result
    return result;
}

