using namespace std;

vector<int> pluck(vector<int> arr) {
    if (arr.empty()) {
        return {};
    }

    int smallest_even_value = numeric_limits<int>::max();
    int smallest_index = -1;

    for (int i = 0; i < arr.size(); ++i) {
        if (arr[i] % 2 == 0 && arr[i] < smallest_even_value) {
            smallest_even_value = arr[i];
            smallest_index = i;
        }
    }

    if (smallest_even_value == numeric_limits<int>::max()) {
        return {};
    }

    return {smallest_even_value, smallest_index};
}

