// Function to check if a vector is a palindrome
bool is_palindrome(const vector<int>& q) {
    int n = q.size();
    for (int i = 0; i < n / 2; ++i) {
        if (q[i] != q[n - i - 1]) {
            return false;
        }
    }
    return true;
}

// Function to calculate the sum of elements in a vector
int sum_vector(const vector<int>& q) {
    int sum = 0;
    for (int num : q) {
        sum += num;
    }
    return sum;
}

// Function to determine if the object q will fly
bool will_it_fly(vector<int> q, int w) {
    if (is_palindrome(q) && sum_vector(q) <= w) {
        return true;
    }
    return false;
}

