int smallest_change(vector<int> arr) {
    int n = arr.size();
    int changes = 0;
    int left = 0;
    int right = n - 1;

    while (left < right) {
        if (arr[left] != arr[right]) {
            changes += 1;
        }
        left += 1;
        right -= 1;
    }

    return changes;
}

