bool iscube(int a) {
    // Handle the special case where a is zero
    if (a == 0) {
        return true;
    }

    // Compute the cube root of the absolute value of a
    double cubeRoot = cbrt(abs(a));

    // Round the cube root to the nearest integer
    int roundedCubeRoot = round(cubeRoot);

    // Check if the cube of the rounded integer equals a
    return pow(roundedCubeRoot, 3) == a;
}

