bool is_simple_power(int x, int n) {
    // Special case: n^0 = 1
    if (x == 1) return true;

    // Special case: 1^k = 1, but 1^k != x if x != 1
    if (n == 1) return false;

    // Calculate the logarithm of x with base n
    double log_result = log(x) / log(n);

    // Check if the result is an integer
    return floor(log_result) == log_result;
}

