string decimal_to_binary(int decimal) {
    string binary = "";

    // Convert decimal to binary
    while (decimal > 0) {
        binary = to_string(decimal % 2) + binary;
        decimal /= 2;
    }

    // Handle the case when the decimal number is 0
    if (binary.empty()) {
        binary = "0";
    }

    // Add "db" at the beginning and the end
    binary = "db" + binary + "db";

    return binary;
}

