int starts_one_ends(int n) {
    if (n == 1) {
        return 1; // Only one 1-digit number starts or ends with 1, which is 1 itself.
    }

    int start_with_1 = pow(10, n - 1);
    int end_with_1 = 9 * pow(10, n - 2);
    int both_start_and_end_with_1 = pow(10, n - 2);

    return start_with_1 + end_with_1 - both_start_and_end_with_1;
}


