string solve(int N) {
    int sum = 0;
    while (N > 0) {
        sum += N % 10; // Add the last digit to sum
        N /= 10;       // Remove the last digit
    }

    // Convert sum to binary
    string binary = "";
    if (sum == 0) {
        return "0";
    }
    while (sum > 0) {
        binary = to_string(sum % 2) + binary; // Prepend the binary digit
        sum /= 2;
    }

    return binary;
}

