string encrypt(string s) {
    string result = "";
    for (char c : s) {
        if (islower(c)) {
            // Apply the shift formula
            char new_char = ((c - 'a' + 4) % 26) + 'a';
            result += new_char;
        } else {
            // If not a lowercase letter, keep the character as is
            result += c;
        }
    }
    return result;
}


