// Function to trim leading and trailing whitespace from a string
string trim(const string& str) {
    size_t first = str.find_first_not_of(' ');
    if (string::npos == first) {
        return str;
    }
    size_t last = str.find_last_not_of(' ');
    return str.substr(first, (last - first + 1));
}

int is_bored(string S) {
    int count = 0;
    stringstream ss(S);
    string sentence;

    // Delimiters for splitting sentences
    char delimiters[] = {'.', '?', '!'};

    while (getline(ss, sentence, '.')) {
        // Further split by '?' and '!'
        stringstream inner_ss(sentence);
        string inner_sentence;

        while (getline(inner_ss, inner_sentence, '?')) {
            stringstream innermost_ss(inner_sentence);
            string innermost_sentence;

            while (getline(innermost_ss, innermost_sentence, '!')) {
                innermost_sentence = trim(innermost_sentence);
                if (!innermost_sentence.empty() && innermost_sentence[0] == 'I' && (innermost_sentence.size() == 1 || innermost_sentence[1] == ' ')) {
                    count++;
                }
            }
        }
    }

    return count;
}


