bool is_prime(int n) {
    if (n <= 1) return false;
    if (n == 2) return true;
    if (n % 2 == 0) return false;
    for (int i = 3; i * i <= n; i += 2) {
        if (n % i == 0) return false;
    }
    return true;
}

int skjkasdkd(vector<int> lst) {
    int largest_prime = 2; // Initialize with the smallest prime number

    // Find the largest prime number in the list
    for (int num : lst) {
        if (num > largest_prime && is_prime(num)) {
            largest_prime = num;
        }
    }

    // Calculate the sum of the digits of the largest prime
    int sum_of_digits = 0;
    while (largest_prime > 0) {
        sum_of_digits += largest_prime % 10;
        largest_prime /= 10;
    }

    return sum_of_digits;
}

