char next_vowel(char vowel) {
    switch (tolower(vowel)) {
        case 'a': return toupper(vowel) == vowel ? 'C' : 'c';
        case 'e': return toupper(vowel) == vowel ? 'G' : 'g';
        case 'i': return toupper(vowel) == vowel ? 'K' : 'k';
        case 'o': return toupper(vowel) == vowel ? 'Q' : 'q';
        case 'u': return toupper(vowel) == vowel ? 'W' : 'w';
        default: return vowel; // In case of non-vowel characters
    }
}

string encode(string message) {
    string result = "";
    for (char c : message) {
        if (isalpha(c)) {
            if (islower(c)) {
                c = toupper(c);
            } else {
                c = tolower(c);
            }
            if (strchr("aeiouAEIOU", c)) {
                c = next_vowel(c);
            }
        }
        result += c;
    }
    return result;
}


