int multiply(int a, int b) {
    // Extract the unit digit of a
    int unitDigitA = abs(a) % 10;
    // Extract the unit digit of b
    int unitDigitB = abs(b) % 10;
    // Compute the product of the unit digits
    int product = unitDigitA * unitDigitB;
    // Return the product
    return product;
}

