using namespace std;

int closest_integer(string value) {
    // Convert the string to a floating-point number
    stringstream ss(value);
    double number;
    ss >> number;

    // Determine the floor and ceiling values
    int floor_value = floor(number);
    int ceil_value = ceil(number);

    // Check if the number is equidistant from two integers
    if (number - floor_value == 0.5) {
        if (number > 0) {
            return ceil_value;
        } else {
            return floor_value;
        }
    } else if (ceil_value - number == 0.5) {
        if (number > 0) {
            return ceil_value;
        } else {
            return floor_value;
        }
    } else {
        // Round to the nearest integer
        return round(number);
    }
}

