bool check_dict_case(map<string, string> dict) {
    if (dict.empty()) {
        return false;
    }

    bool allLower = true;
    bool allUpper = true;

    for (const auto& pair : dict) {
        const string& key = pair.first;
        for (char c : key) {
            if (isupper(c)) {
                allLower = false;
            }
            if (islower(c)) {
                allUpper = false;
            }
        }
    }

    return allLower || allUpper;
}


