vector<int> make_a_pile(int n) {
    vector<int> pile;
    int current_stones = n;

    for (int i = 0; i < n; ++i) {
        pile.push_back(current_stones);
        // Determine the next number of stones
        if (current_stones % 2 == 0) {
            current_stones += 2; // Next even number
        } else {
            current_stones += 2; // Next odd number
        }
    }

    return pile;
}

