int choose_num(int x, int y) {
    // Check if the range is valid
    if (x > y) {
        return -1;
    }

    // Check if y is even
    if (y % 2 == 0) {
        return y;
    }

    // Check if y - 1 is within the range
    if (y - 1 >= x) {
        return y - 1;
    }

    // No even number in the range
    return -1;
}

