// Helper function to check if a number has any even digit
bool hasEvenDigit(int num) {
    while (num > 0) {
        int digit = num % 10;
        if (digit % 2 == 0) {
            return true;
        }
        num /= 10;
    }
    return false;
}

vector<int> unique_digits(vector<int> x) {
    vector<int> result;
    for (int num : x) {
        if (!hasEvenDigit(num)) {
            result.push_back(num);
        }
    }
    sort(result.begin(), result.end());
    return result;
}

