string convert_to_binary(int number) {
    if (number == 0) {
        return "0";
    }
    string binary = "";
    while (number > 0) {
        binary = to_string(number % 2) + binary;
        number = number / 2;
    }
    return binary;
}

string rounded_avg(int n, int m) {
    if (n > m) {
        return "-1";
    }
    int sum = 0;
    int count = 0;
    for (int i = n; i <= m; ++i) {
        sum += i;
        count += 1;
    }
    double average = static_cast<double>(sum) / count;
    int rounded_average = floor(average + 0.5);
    return convert_to_binary(rounded_average);
}


