vector<string> by_length(vector<int> arr) {
    // Step 1: Initialize the map for integer-to-word mapping
    map<int, string> numberWords = {
        {1, "One"}, {2, "Two"}, {3, "Three"}, {4, "Four"},
        {5, "Five"}, {6, "Six"}, {7, "Seven"}, {8, "Eight"}, {9, "Nine"}
    };

    // Step 2: Create a vector to store the filtered integers
    vector<int> filteredArr;

    // Step 3: Filter the input vector
    for (int num : arr) {
        if (num >= 1 && num <= 9) {
            filteredArr.push_back(num);
        }
    }

    // Step 4: Sort the filtered vector
    sort(filteredArr.begin(), filteredArr.end());

    // Step 5: Reverse the sorted vector
    reverse(filteredArr.begin(), filteredArr.end());

    // Step 6: Create a result vector to store the words
    vector<string> result;

    // Step 7: Map each integer to its corresponding word
    for (int num : filteredArr) {
        result.push_back(numberWords[num]);
    }

    // Step 8: Return the result vector
    return result;
}

// Function to test the solution
void test() {
    vector<int> arr1 = {2, 1, 1, 4, 5, 8, 2, 3};
    vector<string> result1 = by_length(arr1);
    for (const string& word : result1) {
        cout << word << " ";
    }
    cout << endl; // Expected: Eight Five Four Three Two Two One One

    vector<int> arr2 = {};
    vector<string> result2 = by_length(arr2);
    for (const string& word : result2) {
        cout << word << " ";
    }
    cout << endl; // Expected: (empty)

    vector<int> arr3 = {1, -1, 55};
    vector<string> result3 = by_length(arr3);
    for (const string& word : result3) {
        cout << word << " ";
    }
    cout << endl; // Expected: One
}


