// Function to compute the factorial of a number
int factorial(int x) {
    if (x == 0 || x == 1) {
        return 1;
    }
    int result = 1;
    for (int j = 2; j <= x; ++j) {
        result *= j;
    }
    return result;
}

// Function to compute the sum of numbers from 1 to x
int sum_of_numbers_from_1_to_i(int x) {
    return (x * (x + 1)) / 2;
}

// Main function to generate the vector
vector<int> f(int n) {
    vector<int> result(n);
    for (int i = 1; i <= n; ++i) {
        if (i % 2 == 0) {
            result[i - 1] = factorial(i);
        } else {
            result[i - 1] = sum_of_numbers_from_1_to_i(i);
        }
    }
    return result;
}

