int count_nums(vector<int> n) {
    int count = 0;
    for (int number : n) {
        int sum_of_digits = 0;
        if (number < 0) {
            number = -number;
            sum_of_digits -= number % 10;
            number /= 10;
        }
        while (number > 0) {
            sum_of_digits += number % 10;
            number /= 10;
        }
        if (sum_of_digits > 0) {
            count++;
        }
    }
    return count;
}

