    assert(result == map<char, int>{{'a', 2}, {'b', 2}});

    result = histogram("a b c a b");
    assert(result == map<char, int>{{'a', 2}, {'b', 2}});

    result = histogram("b b b b a");
    assert(result == map<char, int>{{'b', 4}});

    result = histogram("");
    assert(result.empty());

