string exchange(vector<int> lst1, vector<int> lst2) {
    int count_odd_lst1 = 0;
    int count_even_lst2 = 0;

    // Count the number of odd elements in lst1
    for (int num : lst1) {
        if (num % 2 != 0) {
            count_odd_lst1++;
        }
    }

    // Count the number of even elements in lst2
    for (int num : lst2) {
        if (num % 2 == 0) {
            count_even_lst2++;
        }
    }

    // Check if it's possible to make all elements in lst1 even
    if (count_odd_lst1 <= count_even_lst2) {
        return "YES";
    } else {
        return "NO";
    }
}

