bool isSorted(const vector<int>& arr) {
    for (size_t i = 1; i < arr.size(); ++i) {
        if (arr[i] < arr[i - 1]) {
            return false;
        }
    }
    return true;
}

bool move_one_ball(vector<int> arr) {
    if (arr.empty()) {
        return true;
    }

    size_t n = arr.size();
    size_t breakPoint = -1;

    // Find the break point
    for (size_t i = 1; i < n; ++i) {
        if (arr[i] < arr[i - 1]) {
            if (breakPoint != -1) {
                // More than one break point, cannot be sorted by right shifts
                return false;
            }
            breakPoint = i;
        }
    }

    // If no break point, the array is already sorted
    if (breakPoint == -1) {
        return true;
    }

    // Perform the right shift from the break point
    vector<int> shiftedArr = arr;
    rotate(shiftedArr.rbegin(), shiftedArr.rbegin() + (n - breakPoint), shiftedArr.rend());

    // Check if the shifted array is sorted
    return isSorted(shiftedArr);
}

