vector<string> reverse_delete(string s, string c) {
    // Step 1: Initialize an empty string to store the result
    string result = "";

    // Step 2: Iterate through each character in s
    for (char ch : s) {
        // If the character is not in c, append it to result
        if (c.find(ch) == string::npos) {
            result += ch;
        }
    }

    // Step 3: Check if result is a palindrome
    string reversed = result;
    reverse(reversed.begin(), reversed.end());
    bool is_palindrome = (result == reversed);

    // Step 4: Return a vector containing the result and the palindrome check result
    return {result, is_palindrome ? "True" : "False"};
}

