int max_fill(vector<vector<int>> grid, int capacity) {
    int max_water = 0;
    int num_rows = grid.size();
    int num_cols = grid[0].size();

    // Calculate the maximum number of units of water in any column
    for (int j = 0; j < num_cols; ++j) {
        int current_column_water = 0;
        for (int i = 0; i < num_rows; ++i) {
            current_column_water += grid[i][j];
        }
        max_water = max(max_water, current_column_water);
    }

    // Calculate the total number of lowerings required
    int total_lowerings = (max_water + capacity - 1) / capacity;

    return total_lowerings;
}

