long long minSubArraySum(vector<long long> nums) {
    long long minSum = LLONG_MAX; // Initialize minSum to the largest possible value
    long long currentSum = 0; // Initialize currentSum to 0

    for (long long num : nums) {
        currentSum += num; // Add the current element to currentSum
        if (currentSum < minSum) {
            minSum = currentSum; // Update minSum if currentSum is smaller
        }
        if (currentSum > 0) {
            currentSum = 0; // Reset currentSum if it becomes positive
        }
    }

    return minSum; // Return the minimum sum found
}

