// Function to count the number of consonants in a word
int count_consonants(const string& word) {
    string consonants = "bcdfghjklmnpqrstvwxyzBCDFGHJKLMNPQRSTVWXYZ";
    int count = 0;
    for (char ch : word) {
        if (consonants.find(ch) != string::npos) {
            count++;
        }
    }
    return count;
}

// Function to split a string into words
vector<string> split_string_into_words(const string& s) {
    vector<string> words;
    stringstream ss(s);
    string word;
    while (ss >> word) {
        words.push_back(word);
    }
    return words;
}

// Main function to select words with exactly n consonants
vector<string> select_words(const string& s, int n) {
    vector<string> result;
    vector<string> words = split_string_into_words(s);
    for (const string& word : words) {
        if (count_consonants(word) == n) {
            result.push_back(word);
        }
    }
    return result;
}

// Function to test the implementation
void test_select_words() {
    vector<string> result1 = select_words("Mary had a little lamb", 4);
    assert(result1 == vector<string>{"little"});

    vector<string> result2 = select_words("Mary had a little lamb", 3);
    assert(result2 == vector<string>{"Mary", "lamb"});

    vector<string> result3 = select_words("simple white space", 2);
    assert(result3.empty());

    vector<string> result4 = select_words("Hello world", 4);
    assert(result4 == vector<string>{"world"});

    vector<string> result5 = select_words("Uncle sam", 3);
    assert(result5 == vector<string>{"Uncle"});

    cout << "All tests passed!" << endl;
}


