#include <string>
using namespace std;

string match_parens(vector<string> lst) {
    // Initialize counters for open and close parentheses
    int open1 = 0, close1 = 0, open2 = 0, close2 = 0;

    // Count open and close parentheses in the first string
    for (char c : lst[0]) {
        if (c == '(') open1++;
        else close1++;
    }

    // Count open and close parentheses in the second string
    for (char c : lst[1]) {
        if (c == '(') open2++;
        else close2++;
    }

    // Check if concatenating the first string followed by the second string is balanced
    if (open1 >= close1 && open2 >= close2 && open1 + open2 == close1 + close2) {
        return "Yes";
    }

    // Check if concatenating the second string followed by the first string is balanced
    if (open2 >= close2 && open1 >= close1 && open1 + open2 == close1 + close2) {
        return "Yes";
    }

    // If neither concatenation is balanced, return "No"
    return "No";
}

// Example usage

#undef NDEBUG
#include<assert.h>
int main(){
    assert (match_parens({"()(", ")"}) == "Yes");
    assert (match_parens({")", ")"}) == "No");
    assert (match_parens({"(()(())", "())())"}) == "No");
    assert (match_parens({")())", "(()()("}) == "Yes");
    assert (match_parens({"(())))", "(()())(("}) == "Yes");
