using namespace std;

bool is_sorted(vector<int> lst) {
    if (lst.empty() || lst.size() == 1) {
        return true;
    }

    int previous = lst[0];
    int count = 1;

    for (size_t i = 1; i < lst.size(); ++i) {
        if (lst[i] < previous) {
            return false; // Not sorted
        }
        if (lst[i] == previous) {
            count++;
            if (count > 2) {
                return false; // More than two occurrences
            }
        } else {
            previous = lst[i];
            count = 1;
        }
    }

    return true;
}

#undef NDEBUG
