using namespace std;

// Define a structure to store the path and its current position
struct Path {
    vector<int> values;
    int x, y;
};

// Comparator for the priority queue to ensure lexicographical order
struct Compare {
    bool operator()(const Path& a, const Path& b) const {
        return a.values > b.values;
    }
};

vector<int> minPath(vector<vector<int>>& grid, int k) {
    int N = grid.size();
    priority_queue<Path, vector<Path>, Compare> pq;

    // Add all initial paths of length 1 to the priority queue
    for (int i = 0; i < N; ++i) {
        for (int j = 0; j < N; ++j) {
            Path path = {{grid[i][j]}, i, j};
            pq.push(path);
        }
    }

    // Directions for moving to neighboring cells
    vector<pair<int, int>> directions = {{0, 1}, {1, 0}, {0, -1}, {-1, 0}};

    while (!pq.empty()) {
        Path current = pq.top();
        pq.pop();

        // If the current path length is k, return the path
        if (current.values.size() == k) {
            return current.values;
        }

        // Explore all neighboring cells
        for (auto& dir : directions) {
            int nx = current.x + dir.first;
            int ny = current.y + dir.second;

            // Check if the new position is within the grid
            if (nx >= 0 && nx < N && ny >= 0 && ny < N) {
                Path newPath = current;
                newPath.values.push_back(grid[nx][ny]);
                newPath.x = nx;
                newPath.y = ny;
                pq.push(newPath);
            }
        }
    }

    // This line should never be reached due to the problem guarantee
    return {};
}


