bool valid_date(string date) {
    // Check if the date string is empty
    if (date.empty()) {
        return false;
    }

    // Split the date string by '-'
    stringstream ss(date);
    string token;
    vector<string> parts;
    while (getline(ss, token, '-')) {
        parts.push_back(token);
    }

    // Check if the split results in exactly three parts
    if (parts.size() != 3) {
        return false;
    }

    // Convert the month, day, and year to integers
    int month, day, year;
    try {
        month = stoi(parts[0]);
        day = stoi(parts[1]);
        year = stoi(parts[2]);
    } catch (const invalid_argument& e) {
        return false;
    }

    // Validate the month (1-12)
    if (month < 1 || month > 12) {
        return false;
    }

    // Validate the day based on the month
    if (month == 2) {
        if (day < 1 || day > 29) {
            return false;
        }
    } else if (month == 4 || month == 6 || month == 9 || month == 11) {
        if (day < 1 || day > 30) {
            return false;
        }
    } else {
        if (day < 1 || day > 31) {
            return false;
        }
    }

    // If all checks pass, return true
    return true;
}


#undef NDEBUG
#include<assert.h>
int main(){
    assert (valid_date("03-11-2000") == true);
    assert (valid_date("15-01-2012") == false);
    assert (valid_date("04-0-2040") == false);
    assert (valid_date("06-04-2020") == true);
    assert (valid_date("01-01-2007") == true);
    assert (valid_date("03-32-2011") == false);
    assert (valid_date("") == false);
    assert (valid_date("04-31-3000") == false);
    assert (valid_date("06-06-2005") == true);
    assert (valid_date("21-31-2000") == false);
    assert (valid_date("04-12-2003") == true);
    assert (valid_date("04122003") == false);
    assert (valid_date("20030412") == false);
    assert (valid_date("2003-04") == false);
    assert (valid_date("2003-04-12") == false);
    assert (valid_date("04-2003") == false);
}

