using namespace std;

vector<int> tri(int n) {
    vector<int> result(n + 1, -1); // Initialize result vector with -1

    // Helper function to compute Tribonacci number
    int computeTri(int k, vector<int>& result) {
        if (k == 1) {
            return 3;
        }
        if (result[k] != -1) {
            return result[k];
        }
        if (k % 2 == 0) {
            result[k] = 1 + k / 2;
        } else {
            result[k] = computeTri(k - 1, result) + computeTri(k - 2, result) + computeTri(k + 1, result);
        }
        return result[k];
    }

    // Compute the first n + 1 Tribonacci numbers
    for (int i = 0; i <= n; ++i) {
        result[i] = computeTri(i, result);
    }

    return result;
}

