bool is_nested(string str) {
    stack<char> s;
    bool nested = false;

    for (char c : str) {
        if (c == '[') {
            s.push(c);
        } else if (c == ']') {
            if (s.empty()) {
                return false; // Invalid sequence
            }
            s.pop();
            if (!s.empty()) {
                nested = true; // Found a nested structure
            }
        }
    }

    return nested;
}


