vector<int> largest_smallest_integers(vector<int> lst) {
    int largest_negative = INT_MIN;
    int smallest_positive = INT_MAX;

    for (int num : lst) {
        if (num < 0) {
            if (largest_negative == INT_MIN || num > largest_negative) {
                largest_negative = num;
            }
        } else if (num > 0) {
            if (smallest_positive == INT_MAX || num < smallest_positive) {
                smallest_positive = num;
            }
        }
    }

    // Convert INT_MIN and INT_MAX back to 0 if no valid values were found
    if (largest_negative == INT_MIN) {
        largest_negative = 0;
    }
    if (smallest_positive == INT_MAX) {
        smallest_positive = 0;
    }

    return {largest_negative, smallest_positive};
}

