boost::any compare_one(boost::any a, boost::any b) {
    // Helper function to convert string to float
    float string_to_float(const std::string& str) {
        std::replace(str.begin(), str.end(), ',', '.');
        return std::stof(str);
    }

    // Extract the types of the inputs
    bool is_a_string = a.type() == typeid(std::string);
    bool is_b_string = b.type() == typeid(std::string);

    float a_val, b_val;

    // Convert to float if necessary
    if (is_a_string) {
        a_val = string_to_float(boost::any_cast<std::string>(a));
    } else {
        a_val = boost::any_cast<float>(a);
    }

    if (is_b_string) {
