string fix_spaces(string text) {
    string result = "";
    int consecutive_spaces = 0;

    for (char c : text) {
        if (c == ' ') {
            consecutive_spaces++;
        } else {
            if (consecutive_spaces > 2) {
                result += "-";
            } else {
                result += string(consecutive_spaces, '_');
            }
            result += c;
            consecutive_spaces = 0;
        }
    }

    if (consecutive_spaces > 2) {
        result += "-";
    } else {
        result += string(consecutive_spaces, '_');
    }

    return result;
}


