    if (dot_pos == string::npos || file_name.find('.', dot_pos + 1) != string::npos) {
        return "No";
    }

    // Split the file name into two parts
    string name_part = file_name.substr(0, dot_pos);
    string extension_part = file_name.substr(dot_pos + 1);

    // Check if the name part is not empty and starts with a letter
    if (name_part.empty() || !isalpha(name_part[0])) {
        return "No";
    }

    // Count the digits in the name part
    int digit_count = 0;
    for (char c : name_part) {
        if (isdigit(c)) {
            digit_count++;
        }
    }
    if (digit_count > 3) {
        return "No";
    }

    // Check if the extension part is one of the allowed extensions
    if (extension_part != "txt" && extension_part != "exe" && extension_part != "dll") {
        return "No";
    }

    // If all conditions are met, return "Yes"
    return "Yes";
}


