int sum_squares(vector<int> lst) {
    int sum = 0;
    for (int i = 0; i < lst.size(); ++i) {
        if (i % 3 == 0) {
            sum += lst[i] * lst[i]; // Square the element
        } else if (i % 4 == 0) {
            sum += lst[i] * lst[i] * lst[i]; // Cube the element
        } else {
            sum += lst[i]; // Add the element as is
        }
    }
    return sum;
}

