string words_in_sentence(string sentence) {
    stringstream ss(sentence);
    string word;
    string result;
    while (ss >> word) {
        if (is_prime(word.length())) {
            if (!result.empty()) result += " ";
            result += word;
        }
    }
    return result;
}

