// Function to parse the fraction string and return the numerator and denominator
pair<int, int> parseFraction(const string& fraction) {
    stringstream ss(fraction);
    string part;
    int numerator, denominator;
    getline(ss, part, '/'); // Read the numerator
    numerator = stoi(part);
    getline(ss, part); // Read the denominator
    denominator = stoi(part);
    return {numerator, denominator};
}

bool simplify(string x, string n) {
    // Parse the fractions
    pair<int, int> xFraction = parseFraction(x);
    pair<int, int> nFraction = parseFraction(n);

    // Extract numerators and denominators
    int xNumerator = xFraction.first;
    int xDenominator = xFraction.second;
    int nNumerator = nFraction.first;
    int nDenominator = nFraction.second;

    // Multiply the numerators and denominators
    int resultNumerator = xNumerator * nNumerator;
    int resultDenominator = xDenominator * nDenominator;

    // Check if the result is a whole number
    return (resultNumerator % resultDenominator == 0);
}


