int get_max_triples(int n) {
    // Step 1: Initialize the vector a
    vector<int> a(n);

    // Step 2: Populate the vector a
    for (int i = 1; i <= n; ++i) {
        a[i-1] = i * i - i + 1;
    }

    // Step 3: Initialize the counter
    int count = 0;

    // Step 4: Iterate through all possible triples (i, j, k)
    for (int i = 0; i < n; ++i) {
        for (int j = i + 1; j < n; ++j) {
            for (int k = j + 1; k < n; ++k) {
                // Step 5: Check if the sum is a multiple of 3
                if ((a[i] + a[j] + a[k]) % 3 == 0) {
                    count++;
                }
            }
        }
    }

    // Step 6: Return the count
    return count;
}

