// Helper function to calculate the sum of digits of an integer
int sum_of_digits(int num) {
    int sum = 0;
    bool isNegative = num < 0;
    while (num != 0) {
        int digit = num % 10;
        if (isNegative && num < 0) {
            digit = -digit;
        }
        sum += digit;
        num /= 10;
    }
    return sum;
}

// Custom comparator for sorting pairs
bool compare(const std::pair<int, int>& a, const std::pair<int, int>& b) {
    if (a.second == b.second) {
        return a.first < b.first;
    }
    return a.second < b.second;
}

std::vector<int> order_by_points(std::vector<int> nums) {
    // Create a vector of pairs (integer, sum of digits)
    std::vector<std::pair<int, int>> pairs;
    for (int i = 0; i < nums.size(); ++i) {
        pairs.push_back({nums[i], sum_of_digits(nums[i])});
    }

    // Sort the pairs using the custom comparator
    std::sort(pairs.begin(), pairs.end(), compare);

    // Extract the sorted integers
    std::vector<int> sorted_nums;
    for (const auto& p : pairs) {
        sorted_nums.push_back(p.first);
    }

    return sorted_nums;
}

