using namespace std;

vector<string> sorted_list_sum(vector<string> lst) {
    // Step 1: Filter out strings with odd lengths
    vector<string> result;
    for (const string& str : lst) {
        if (str.length() % 2 == 0) {
            result.push_back(str);
        }
    }

    // Step 2: Sort the result vector
    // First, sort by length
    sort(result.begin(), result.end(), [](const string& a, const string& b) {
        if (a.length() == b.length()) {
            return a < b; // Alphabetical order if lengths are equal
        }
        return a.length() < b.length(); // Length order
    });

    return result;
}

// Function to test the sorted_list_sum function
void test_sorted_list_sum() {
    assert((sorted_list_sum({"aa", "a", "aaa"}) == vector<string>{"aa"}));
    assert((sorted_list_sum({"ab", "a", "aaa", "cd"}) == vector<string>{"ab", "cd"}));
    assert((sorted_list_sum({"abc", "def", "ghi", "jkl"}) == vector<string>{}));
    assert((sorted_list_sum({"one", "two", "three", "four", "five"}) == vector<string>{"one", "four"}));
    cout << "All tests passed!" << endl;
}


